digraph RefModel {
  rankdir=BT;
  node [shape=record, style=filled, fillcolor=white, width=4];

  L4 [label="Layer 4: Application (SaaS)\n(Users: End Consumers)\nEx: Gmail, Salesforce, Dropbox", fillcolor=lightgreen];
  L3 [label="Layer 3: Platform (PaaS)\n(Users: Developers)\nEx: Google App Engine, Heroku", fillcolor=lightblue];
  L2 [label="Layer 2: Infrastructure (IaaS)\n(Users: Architects/Admins)\nEx: AWS EC2, Azure VM", fillcolor=lightsalmon];
  L1 [label="Layer 1: Physical Infrastructure\n(Users: Providers)\nEx: Data Centers, Servers, Network Cables", fillcolor=lightgrey];

  L1 -> L2 -> L3 -> L4;
}

graph UtilityChar {
  node [shape=box, style=filled];
