  subgraph cluster_Util {
    label="Utility Model";
    style=dashed;
    U1 [label="Pay-per-use"];
    U2 [label="Metered Billing"];
    U3 [label="OpEx > CapEx"];
  }

  subgraph cluster_Char {
    label="5 NIST Characteristics";
    style=soild;
    C1 [label="1. On-Demand Self-Service", fillcolor=lightcyan];
    C2 [label="2. Broad Network Access", fillcolor=lightcyan];
    C3 [label="3. Resource Pooling", fillcolor=lightcyan];
    C4 [label="4. Rapid Elasticity", fillcolor=lightcyan];
    C5 [label="5. Measured Service", fillcolor=lightcyan];
  }
  Root -> U2;
  Root -> C1;
  C1 -> C2 -> C3 -> C4 -> C5 [style=invis];
}

digraph Flynns {
  rankdir=LR;
  node [shape=record, style=filled];

  SISD [label="SISD\n(Serial)\n1 Stream -> 1 Data", fillcolor=lightgrey];
  SIMD [label="SIMD\n(Parallel Data)\n1 Stream -> N Data", fillcolor=lightblue];
  MISD [label="MISD\n(Pipeline/Safety)\nN Streams -> 1 Data", fillcolor=lightpink];
  MIMD [label="MIMD\n(True Parallel)\nN Streams -> N Data", fillcolor=lightgreen];

  SISD -> SIMD [style=invis];
  SIMD -> MISD [style=invis];
  MISD -> MIMD [style=invis];
}

