graphviz:
digraph RefModel {
  rankdir=BT;
  node [shape=record, style=filled, fillcolor=white, width=4];

  L4 [label="Layer 4: Application (SaaS)\n(Users: End Consumers)\nEx: Gmail, Salesforce, Dropbox", fillcolor=lightgreen];
  L3 [label="Layer 3: Platform (PaaS)\n(Users: Developers)\nEx: Google App Engine, Heroku", fillcolor=lightblue];
  L2 [label="Layer 2: Infrastructure (IaaS)\n(Users: Architects/Admins)\nEx: AWS EC2, Azure VM", fillcolor=lightsalmon];
  L1 [label="Layer 1: Physical Infrastructure\n(Users: Providers)\nEx: Data Centers, Servers, Network Cables", fillcolor=lightgrey];

  L1 -> L2 -> L3 -> L4;
}

graph UtilityChar {
  node [shape=box, style=filled];
  Root [label="Cloud Essentials", fillcolor=gold];

  subgraph cluster_Util {
    label="Utility Model";
    style=dashed;
    U1 [label="Pay-per-use"];
    U2 [label="Metered Billing"];
    U3 [label="OpEx > CapEx"];
  }

  subgraph cluster_Char {
    label="5 NIST Characteristics";
    style=soild;
    C1 [label="1. On-Demand Self-Service", fillcolor=lightcyan];
    C2 [label="2. Broad Network Access", fillcolor=lightcyan];
    C3 [label="3. Resource Pooling", fillcolor=lightcyan];
    C4 [label="4. Rapid Elasticity", fillcolor=lightcyan];
    C5 [label="5. Measured Service", fillcolor=lightcyan];
  }
  Root -> U2;
  Root -> C1;
  C1 -> C2 -> C3 -> C4 -> C5 [style=invis];
}

digraph SPI_Stack {
  rankdir=LR;
  node [shape=record];

  IaaS [label="{IaaS Model|User Manages:|Applications|Data|Runtime|Middleware|O/S}|{Provider Manages:|Virtualization|Servers|Storage|Networking}", fillcolor=salmon, style=filled];

  PaaS [label="{PaaS Model|User Manages:|Applications|Data}|{Provider Manages:|Runtime|Middleware|O/S|Virtualization|Servers|Storage|Networking}", fillcolor=lightblue, style=filled];
}

graph DeploymentSketches {
  layout=neato;
  node [shape=box, style=filled];

  Public [label="Public Cloud\n(The Internet)\nMulti-Tenant", fillcolor=lightgreen, pos="0,0!"];
  User1 [label="User A", shape=ellipse];
  User2 [label="User B", shape=ellipse];
  User1 -- Public;
  User2 -- Public;

  Private [label="Private Cloud\n(On-Premise)\nSingle Org", fillcolor=lightsalmon, pos="4,0!"];
  OrgUser [label="Employees", shape=ellipse];
  OrgUser -- Private;

  Hybrid [label="Hybrid Connection\n(VPN / DirectConnect)", shape=diamond, style=dashed, pos="2,-1!"];
  Public -- Hybrid -- Private;
}

digraph BenefitsMap {
  rankdir=LR;
  node [shape=note, style=filled, fillcolor=lightyellow];
  Cloud [label="Cloud Computing", fillcolor=gold];

  B1 [label="Cost Savings\n(No Hardware)"];
  B2 [label="Global Scale\n(Any Region)"];
  B3 [label="Agility\n(Fast Deploy)"];
  B4 [label="Reliability\n(Backup/DR)"];

  Cloud -> B1;
  Cloud -> B2;
  Cloud -> B3;
  Cloud -> B4;
}

digraph NIST_Ref {
  rankdir=TB;
  node [shape=box];

  Auditor [label="Cloud Auditor\n(Security/Privacy)", style=dashed];
  Broker [label="Cloud Broker\n(Aggregation)", style=dashed];

  subgraph cluster_Provider {
    label="Cloud Provider";
    style=filled; fillcolor=lightgrey;
    Orchestration [label="Service Orchestration"];
    Resource [label="Physical Resource Layer"];
    SaaS -> PaaS -> IaaS -> Resource;
  }

  Consumer [label="Cloud Consumer\n(User)", shape=ellipse, style=filled, fillcolor=lightgreen];
  Carrier [label="Cloud Carrier\n(Internet/ISP)", shape=hexagon];

  Consumer -> Carrier -> SaaS;
  Auditor -> SaaS [label="Audit"];
  Consumer -> Broker -> SaaS;
}

graph CSPComparison {
  rankdir=LR;
  node [shape=record, style=filled];

  AWS [label="AWS\n(Breadth)\nStartups & General", fillcolor=orange];
  Azure [label="Azure\n(Integration)\nCorporate & Hybrid", fillcolor=lightblue];
  GCP [label="GCP\n(Innovation)\nData & AI", fillcolor=lightgreen];

  Market [label="Cloud Market", shape=ellipse, fillcolor=gold];
  Market -> AWS [label="#1 Leader"];
  Market -> Azure [label="#2 Challenger"];
  Market -> GCP [label="#3 Innovator"];
}
