graphviz:
digraph Mechanisms {
  rankdir=LR;
  node [shape=box, style=filled, fillcolor=lightyellow];

  Mech [label="Security Mechanisms", shape=ellipse, fillcolor=gold];

  Enc [label="Encipherment"];
  DigSig [label="Digital Signature"];
  Acc [label="Access Control"];
  Int [label="Data Integrity"];
  Auth [label="Auth Exchange"];

  Mech -> Enc;
  Mech -> DigSig;
  Mech -> Acc;
  Mech -> Int;
  Mech -> Auth;
}

graphviz_a:
digraph EEA {
  rankdir=TB;
  node [shape=box];
  Start [label="Start inputs a, b"];
  Euclid [label="Standard Euclidean steps\nto find GCD"];
  BackSub [label="Back Substitute remainders\nto find s and t"];
  Result [label="Result:\n gcd, s, t"];

  Start -> Euclid -> BackSub -> Result;
}

graph CIA {
  layout=neato;
  node [shape=circle, style=filled, fillcolor=lightgreen, width=1.5];
  C [label="Confidentiality", pos="0,2!"];
  I [label="Integrity", pos="2,-1.5!"];
  A [label="Availability", pos="-2,-1.5!"];

  C -- I -- A -- C [penwidth=2];
  label="The CIA Triad";
}

digraph Euclidean {
  node [shape=box];
  Start [label="Input A, B"];
  Calc [label="R = A % B"];
  Check [label="Is R == 0?", shape=diamond];
  Swap [label="A = B\nB = R"];
  Found [label="GCD is B", style=filled, fillcolor=green];

  Start -> Calc;
  Calc -> Check;
  Check -> Found [label="Yes"];
  Check -> Swap [label="No"];
  Swap -> Calc;
}
