graphviz:
digraph NR {
  rankdir=LR;
  node [shape=box, style=filled, fillcolor=lightcyan];
  Sender [label="Sender\n(Signs Msg)"];
  Receiver [label="Receiver\n(Verifies Msg)"];

  Sender -> Receiver [label="Message + Signature"];
  Sender -> Sender [label="Cannot Deny", color=red, style=dashed];
  Receiver -> Receiver [label="Cannot Deny", color=red, style=dashed];

  label="Non-Repudiation Mechanism";
}

digraph Inverse {
  node [shape=box];
  Input [label="Given a, n"];
  Check [label="Calculate gcd(a,n)", shape=diamond, style=filled, fillcolor=lightyellow];
  Yes [label="gcd == 1\nInverse Exists", style=filled, fillcolor=lightgreen];
  No [label="gcd > 1\nNo Inverse", style=filled, fillcolor=lightpink];

  Input -> Check;
  Check -> Yes [label="Coprime"];
  Check -> No [label="Not Coprime"];
}

digraph Attacks {
  node [shape=record, style=filled, fillcolor=white];
  Attacks [label="Security Attacks", fillcolor=lightgrey];
  Passive [label="Passive Attack|{<f0> Release Msg Contents|<f1> Traffic Analysis}"];
  Active [label="Active Attack|{<f0> Masquerade|<f1> Replay|<f2> Modification|<f3> DOS}"];

  Attacks -> Passive;
  Attacks -> Active;
}

digraph Zn {
  layout=circo;
  node [shape=circle, style=filled, fillcolor=lightblue];
  0 -> 1 -> 2 -> "..." -> "n-1";
  "n-1" -> 0 [label="Wrap"];
  label="Cyclic Nature of Zn";
}

digraph Clock {
  layout=neato;
  node [shape=circle, fixedsize=true, width=0.6];
