graphviz:
digraph Group {
  rankdir=LR;
  node [shape=box, style=filled, fillcolor=lightcyan];
  Set [label="Set G"];
  Op [label="Operation •"];
  Result [label="a • b ∈ G\n(Closure)"];
  Set -> Op -> Result;
}

digraph Product {
  rankdir=LR;
  node [shape=box];
  In [label="Plaintext"];
  S [label="Substitution\n(Confusion)"];
  P [label="Permutation\n(Diffusion)"];
  Out [label="Ciphertext"];
  In -> S -> P -> Out;
  label="Basic Round";
}

digraph Avalanche {
  rankdir=LR;
  In1 [label="Input: ...000"];
  In2 [label="Input: ...001"];
  Box [label="Encryption", shape=box];
  Out1 [label="101100..."];
  Out2 [label="010011..."];
  Diff [label="Totally Different\n(>50% flipped)", style=dashed];

  In1 -> Box -> Out1;
  In2 -> Box -> Out2;
  Out1 -> Diff;
  Out2 -> Diff;
}

graph CD {
  node [shape=oval];
  Confusion [style=filled, fillcolor=salmon];
  Diffusion [style=filled, fillcolor=lightblue];

  Confusion -- "Substitution";
  Diffusion -- "Permutation";
}

digraph Symm {
  rankdir=LR;
  node [shape=box];
  Keys [label="Shared Secret Key", shape=key, style=filled, fillcolor=gold];
  Sender -> Encrypt -> Ciphertext -> Decrypt -> Receiver;
  Keys -> Encrypt;
  Keys -> Decrypt;
}

digraph AES {
  rankdir=LR;
  node [shape=rect, style=filled, fillcolor=lightyellow];
  Sub -> Shift -> Mix -> Key;
  Sub [label="SubBytes"];
  Shift [label="ShiftRows"];
  Mix [label="MixColumns"];
  Key [label="AddRoundKey"];
}
